contracts/rewards.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";


interface  StakingToken {
    function createStake(uint256 _stakeAmount,uint256 _duration) external ;
    function updateStake(uint256 _stakeId,uint256 _addStake) external  returns(bool);
    function getStackerLevel(address _stakeholder) external  view returns(string memory);
    function removeStake(uint256 _stakeId,uint256 _stake) external  returns(bool);
    function stakeOf(address _stakeholder) external view returns(uint256);
    function totalStakes() external view returns(uint256);
    function rewardOf(address _stakeholder) external view returns(uint256);
    function getStartTimeOfStake(uint256 _stakeId) view external  returns(uint256);
    function getHolderByStakeId(uint256 _stakeId) external  view returns(address);
    function isDurationBound(uint256 _stakeId) external  view returns(bool);
    function getStakeAmount(uint256 _stakeId) external  view returns(uint256);
    function getDuration(uint256 _stakeId) external  view returns(uint256);
    function getTotalNumberOfStakes() external view returns(uint256);
    function getStakedPeriod(uint256 _stakeId) external view returns(uint256);
    function getAddedEpoch(uint256 _stakeId) external returns(uint256);
    function getstakesTillEpoch(uint256 epochNumber) external view returns(uint256);
    function getactiveStakesTillEpoch(uint256 epoch) external view returns(uint256);
}

contract rewardPool is Ownable{
    uint256 public POOL_START_TIME;
    uint256 public DISTRIBUTION_CYCLE;
    uint256 constant EPOCH_TIME=604800; // 1 WEEK
   // uint256 constant EPOCH_TIME= 120;
    uint256 constant WEEK_TIME= 604800;
    uint256 constant ONE_DAY =  86400;
    //uint256 constant WEEK_TIME= 120;
    uint256 constant DISTRIBUTION_TIME = 604800*2;
   uint256 MIN_STACKING_PERIOD = 2419200; // 4 weeks time
   //uint256 MIN_STACKING_PERIOD = 240;
    uint256 MAX_STACKING_PERIOD = 13305600;
    address TOKEN_ADDRESS;
    address STAKING_CONTRACT;
    uint256 public CURRENT_EPOCH;
    address EXCHANGE_ADDRESS;
    address USDT_ADDRESS;
    mapping(uint256 => uint256) public  EPOCH_TO_START_TIME;
    mapping(uint256 => uint256) public EPOCH_START_BALANCE;
    mapping(uint256 => uint256) public  ACCUMALATED_REWARDS;
    mapping(uint256 => bool) isDistributed;
    mapping(uint256 => uint256) public CLAIMABLE_REWARDS;
    mapping(uint256 => uint256) DURATION_FACTOR;
    mapping(uint256 => uint256) public CLAIMED_REWARDS;
    address TREASURY;
    mapping(uint256 => uint256) rewardCounter;
    mapping(uint256 => uint256) totalRewardWeight;
    mapping(uint256 => mapping(uint256 => bool)) rewardCalculated;
    mapping(bytes => bool) usedSign;


    uint256 MAX_YIELD = 9079502202;



    constructor(address _token,address _stackingAddress,address _exchange,address _usdt,address _treasury)
    {
        TOKEN_ADDRESS=_token;
        CURRENT_EPOCH=0;
        DISTRIBUTION_CYCLE=0;
        STAKING_CONTRACT= _stackingAddress;
        EXCHANGE_ADDRESS = _exchange;
        USDT_ADDRESS = _usdt;
        TREASURY = _treasury;
    }
    modifier epochFinished{
        require(block.timestamp >= EPOCH_TO_START_TIME[CURRENT_EPOCH]+EPOCH_TIME," epoch not completed");
        _;
    }
    modifier poolStarted{
        require(CURRENT_EPOCH != 0 && DISTRIBUTION_CYCLE != 0,
                "POOL NOT STARTED");
        _;
    }

    modifier userAccess
    {
        uint256 epochEndTime = EPOCH_TO_START_TIME[CURRENT_EPOCH]+EPOCH_TIME;
        require( block.timestamp >= epochEndTime 
                && block.timestamp <= epochEndTime+ONE_DAY,"ACCESS RESTRICTED FOR USERS NOW");
        _;
    }

    modifier adminAccess
    {
        uint256 userAccessEndTime = EPOCH_TO_START_TIME[CURRENT_EPOCH]+EPOCH_TIME + ONE_DAY;
        require( block.timestamp > userAccessEndTime 
                && block.timestamp <= userAccessEndTime+ONE_DAY,"ACCESS RESTRICTED FOR ADMIN NOW");
        _;
    }
    function startPool() onlyOwner public returns(bool)
    {
       require(CURRENT_EPOCH == 0 && DISTRIBUTION_CYCLE== 0 ,
                "POOL ALREADY STARTED");
       CURRENT_EPOCH = 1;
       DISTRIBUTION_CYCLE = 1;
       POOL_START_TIME=block.timestamp;
       EPOCH_TO_START_TIME[CURRENT_EPOCH]=POOL_START_TIME;
       EPOCH_START_BALANCE[CURRENT_EPOCH] = IERC20(TOKEN_ADDRESS).balanceOf(address(this));
       isDistributed[DISTRIBUTION_CYCLE] = true;
      return true;
    }

    function finishEpoch() onlyOwner epochFinished public returns(bool){
        require(rewardCounter[CURRENT_EPOCH]==StakingToken(STAKING_CONTRACT).getactiveStakesTillEpoch(CURRENT_EPOCH),"ALL STAKE REWARDS ARE NOT CALCULATED");
        CURRENT_EPOCH = CURRENT_EPOCH + 1;
        EPOCH_TO_START_TIME[CURRENT_EPOCH]=EPOCH_TO_START_TIME[CURRENT_EPOCH-1]+EPOCH_TIME;
        if(CURRENT_EPOCH % 2 == 0 && CURRENT_EPOCH != 0)
        {
            DISTRIBUTION_CYCLE = DISTRIBUTION_CYCLE + 1;
            isDistributed[DISTRIBUTION_CYCLE] = true;
        }
        return true;

    }

    function rewardOfStake(uint256 _rewards,uint256 _stakeId,bytes memory signature) public   poolStarted userAccess returns(bool){
        require(!usedSign[signature],"signature can be used only once");
        require( msg.sender == StakingToken(STAKING_CONTRACT).getHolderByStakeId(_stakeId),"not stake holder");
        require( ! rewardCalculated[_stakeId][CURRENT_EPOCH] , "REWARD FOR THIS EPOCH IS SET");
        require( StakingToken(STAKING_CONTRACT).getStakeAmount(_stakeId) > 0 ,"STAKE DOESNT EXISTS");
        address signer =verifySign(signature, _rewards, _stakeId, CURRENT_EPOCH);
        require(signer == owner(),"signature mismatch");
        ACCUMALATED_REWARDS[_stakeId] = ACCUMALATED_REWARDS[_stakeId]+_rewards;
        rewardCalculated[_stakeId][CURRENT_EPOCH]=true;
        if(CURRENT_EPOCH % 2 == 0 && CURRENT_EPOCH != 0){
              uint256 _rewardsGiven = getRewardsToGive(_stakeId);
              CLAIMABLE_REWARDS[_stakeId] = _rewardsGiven;
              CLAIMABLE_REWARDS[_stakeId] -= CLAIMED_REWARDS[_stakeId];
        }
        rewardCounter[CURRENT_EPOCH]++;
        usedSign[signature]= true;
        return true;
    }

    function rewardOfStakeByAdmin(uint256 _stakeId,uint256 _rewards, uint256 _gasFeePenalty) public onlyOwner poolStarted adminAccess returns(bool){
        require( ! rewardCalculated[_stakeId][CURRENT_EPOCH] , "REWARD FOR THIS EPOCH IS SET");
        require( StakingToken(STAKING_CONTRACT).getStakeAmount(_stakeId) > 0 ,"STAKE DOESNT EXISTS");
        _rewards = _rewards - _gasFeePenalty;
        IERC20(TOKEN_ADDRESS).transfer(owner(),_gasFeePenalty);
        ACCUMALATED_REWARDS[_stakeId] = ACCUMALATED_REWARDS[_stakeId]+_rewards;
        rewardCalculated[_stakeId][CURRENT_EPOCH]=true;
        if(CURRENT_EPOCH % 2 == 0 && CURRENT_EPOCH != 0){
              uint256 _rewardsGiven = getRewardsToGive(_stakeId);
              CLAIMABLE_REWARDS[_stakeId] = _rewardsGiven;
              CLAIMABLE_REWARDS[_stakeId] -= CLAIMED_REWARDS[_stakeId];
        }
        rewardCounter[CURRENT_EPOCH]++;
        return true;

    } 

    function getRewardsToGive(uint256 _stakeId) internal view returns(uint256){
        uint256 _rewards = ACCUMALATED_REWARDS[_stakeId];
        uint256 stakedPeriod = block.timestamp - StakingToken(STAKING_CONTRACT).getStartTimeOfStake(_stakeId);
        if(stakedPeriod < MIN_STACKING_PERIOD ) return (_rewards*50)/(100);    
       else if (stakedPeriod >= MIN_STACKING_PERIOD && stakedPeriod < 2*MIN_STACKING_PERIOD)
       {
           return (_rewards*60)/(100);
       }
       else if (stakedPeriod >= 2*MIN_STACKING_PERIOD && stakedPeriod < 3*MIN_STACKING_PERIOD)
       {
           return (_rewards*70)/(100); 
       }
       else if (stakedPeriod >= 3*MIN_STACKING_PERIOD && stakedPeriod < 4*MIN_STACKING_PERIOD)
       {
           return (_rewards*80)/(100);
       }
       else if (stakedPeriod >= 4*MIN_STACKING_PERIOD && stakedPeriod < MAX_STACKING_PERIOD)
       {
           return (_rewards*90)/(100);
       }
       else return _rewards;

    }    

    function claimRewards(uint256 _stakeId,uint256 _rewardToClaim) public returns(bool){
        require(msg.sender == StakingToken(STAKING_CONTRACT).getHolderByStakeId(_stakeId),"ONLY HOLDER CLAIM REWARDS");
        require(_rewardToClaim <= CLAIMABLE_REWARDS[_stakeId],"cant withdraw more than claimble rewards");
        CLAIMED_REWARDS[_stakeId] += _rewardToClaim;
        CLAIMABLE_REWARDS[_stakeId] -= _rewardToClaim;
        IERC20(TOKEN_ADDRESS).transfer(msg.sender,_rewardToClaim);
        return true;

    }

    function getDuration(uint256 _stakeId) view public returns(uint256){
       return DURATION_FACTOR[_stakeId];
    }


      function getCurrentEpoch() view  public returns(uint256){
          return CURRENT_EPOCH;
      }

    function getRewardPenalty(uint256 _rewards,uint256 stakedPeriod,uint256 _removedStake,uint256 _totalStake) public  view returns(uint256 penalty){
       if (stakedPeriod >= MIN_STACKING_PERIOD && stakedPeriod < 2*MIN_STACKING_PERIOD)
       {
           penalty = (_rewards*60* _removedStake )/(_totalStake);
       }
       else if (stakedPeriod >= 2*MIN_STACKING_PERIOD && stakedPeriod < 3*MIN_STACKING_PERIOD)
       {
           penalty= (_rewards*30*_removedStake)/( _totalStake); 
       }
       else if (stakedPeriod >= 3*MIN_STACKING_PERIOD && stakedPeriod < 4*MIN_STACKING_PERIOD)
       {
           penalty= (_rewards*20*_removedStake)/( _totalStake);
       }
       else if (stakedPeriod >= 4*MIN_STACKING_PERIOD && stakedPeriod < MAX_STACKING_PERIOD)
       {
           penalty = (_rewards*10*_removedStake)/( _totalStake);
       }

    }
    function imposeRewardPenalty(uint256 _stakeId,uint256 stakedPeriod,uint256 _removedStake,uint256 _totalStake) external returns(bool){
        require(msg.sender == STAKING_CONTRACT,"Only stacking contract can impose penalty");
        uint256 _rewards = ACCUMALATED_REWARDS[_stakeId];
        uint256 _penalty = getRewardPenalty(_rewards, stakedPeriod, _removedStake, _totalStake)/100;
        uint256 rewardsToBeTransferedNow = ( ACCUMALATED_REWARDS[_stakeId] * _removedStake)/(_totalStake);
        rewardsToBeTransferedNow -= _penalty ;
        IERC20(TOKEN_ADDRESS).transfer(TREASURY,_penalty);
        ACCUMALATED_REWARDS[_stakeId] -= rewardsToBeTransferedNow;
        ACCUMALATED_REWARDS[_stakeId] -= _penalty;
        address holder = StakingToken(STAKING_CONTRACT).getHolderByStakeId(_stakeId);
        IERC20(TOKEN_ADDRESS).transfer(holder,rewardsToBeTransferedNow);
        return true;
    }

    function totalStakeRemoved(uint256 _stakeId) external returns(bool) {
        require(msg.sender == STAKING_CONTRACT,"not stacking contract");
        CLAIMABLE_REWARDS[_stakeId] += ACCUMALATED_REWARDS[_stakeId];
        return true; 
    }




    //verifying functions 

      function verifySign(bytes memory sig,uint256 rewards,uint256 stakeId,uint256 currentEpoch) public view returns(address){
         uint chainId;
         assembly {
         chainId := chainid()
       }
     bytes32 eip712DomainHash = keccak256(
        abi.encode(
            keccak256(
                "EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)"
            ),
            keccak256(bytes("volaryTokenomics")),
            keccak256(bytes("1")),
            chainId,
            address(this)
        )
    );  

    bytes32 hashStruct = keccak256(
      abi.encode(
          keccak256("volarySign(uint256 rewards,uint256 stakeId,uint256 currentEpoch)"),
          rewards,
          stakeId,
          currentEpoch
        )
    );

    bytes32 hash = keccak256(abi.encodePacked("\x19\x01", eip712DomainHash, hashStruct));

    (bytes32 r, bytes32 s, uint8 v) = splitSignature(sig);
    address signer = ecrecover(hash, v, r, s);
    return signer;
    }

    function splitSignature(
        bytes memory sig
    ) public pure returns (bytes32 r, bytes32 s, uint8 v) {
        require(sig.length == 65, "invalid signature length");

        assembly {

            r := mload(add(sig, 32))
            s := mload(add(sig, 64))
            v := byte(0, mload(add(sig, 96)))
        }
    }

     function getChainId() public view returns(uint){

     uint chainId;
       assembly {
         chainId := chainid()
       }

       return  chainId;

    }


}

contracts/stacking.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

interface rewardPool{
    function imposeRewardPenalty(uint256 _stakeId,uint256 stakedPeriod,uint256 _removedStake,uint256 _totalStake) external returns(bool);
    function getCurrentEpoch() external view returns(uint256);
    function getDuration(uint256 _stakeId) view external returns(uint256);
    function totalStakeRemoved(uint256 _stakeId) external returns(bool);
}
contract StakingToken is Ownable {
    uint256 stakeId=0;
    uint256 constant MAX_STACKING_PERIOD = 13305600;
    mapping(address => bool) public isStakeHolder;
    mapping(address => uint256) internal rewards;
    mapping(address => uint256) internal recentWithdrawTime;
    mapping(address => string) stackerLevel;
    mapping(uint256 => bool) isPenalized;
    mapping(uint256 => uint256) penalizedStakePeriod;
    mapping(uint256 => uint256) addedInEpoch;

    address rewardPoolAddress;
    address tokenContract;
    address treasury;
    uint256 SILVER_LEVEL_TOKENS = 1667 * 10 ** 18;
    struct stake{
        address holder;
        uint256 stakeAmount;
        uint256 duration;
        uint256 stackingStartTime;
    }
    mapping(uint256 => stake) public getStakeById;
    mapping(address => uint256[]) public addressToStakes;
    mapping(uint256 => uint256) public stakedRemovedTime;
    mapping(uint256 => uint256) public stakesAddedInEpoch;
    mapping(uint256 => uint256) public stakesRemovedInEpoch;
    constructor(address _tokenContract) {
        treasury = msg.sender;
        tokenContract = _tokenContract;
    }


    function createStake(uint256 _stakeAmount,uint256 _duration)
        public
    { 
        require(rewardPoolAddress != address(0),"REWARD POOL NOT ADDED");
        uint256 CURRENT_EPOCH = rewardPool(rewardPoolAddress).getCurrentEpoch();
        addedInEpoch[stakeId] = CURRENT_EPOCH;
        stakesAddedInEpoch[CURRENT_EPOCH]++;
        bool result=IERC20(tokenContract).transferFrom(msg.sender,address(this),_stakeAmount);
        require(result,"stake transfer failed");
        stake storage newStake=getStakeById[stakeId];
        newStake.holder = msg.sender;
        newStake.stakeAmount = _stakeAmount;
        newStake.duration = _duration;
        newStake.stackingStartTime = block.timestamp;
        addressToStakes[msg.sender].push(stakeId);
        stakeId++;
        stackerLevel[msg.sender]=getStackerLevel(msg.sender);
        isStakeHolder[msg.sender]=true;

    }
    function getStackerLevel(address _stakeholder) public view returns(string memory level) {
    uint256 _stakeAmount = stakeOf(_stakeholder);
    if(_stakeAmount < SILVER_LEVEL_TOKENS)  level="none";
    else if (_stakeAmount  >= SILVER_LEVEL_TOKENS*1000)  level="platinum";
    else if (_stakeAmount  >= SILVER_LEVEL_TOKENS*100)  level="diamond";
    else if (_stakeAmount  >= SILVER_LEVEL_TOKENS*10)  level="gold";
    else   level="sliver";

}



    function removeStake(uint256 _stakeId,uint256 _stake)
        public returns(bool)
    {
        uint256 _totalStake=getStakeAmount(_stakeId);
        require(msg.sender == getHolderByStakeId(_stakeId),"only stake holder can remove the stake");
        require(_stake <= _totalStake,"can remove only amount less than staked");
        uint256 CURRENT_EPOCH = rewardPool(rewardPoolAddress).getCurrentEpoch();
        if( _stake == _totalStake)
        {
            stakesRemovedInEpoch[CURRENT_EPOCH]++;
            (bool success)=rewardPool(rewardPoolAddress).totalStakeRemoved(_stakeId);
            require(success,"staking : error while removing entire stake");

        }
        uint256 penalty=0;
        uint256 stakedPeriod= block.timestamp - getStartTimeOfStake(_stakeId);
        if(stakedPeriod < MAX_STACKING_PERIOD)
        {
            if(isDurationBound(_stakeId)){ 
                uint256 _factor = rewardPool(rewardPoolAddress).getDuration(_stakeId);
                penalty = (_factor*getPrincipalPenalty(_stake))/(10**6);
                IERC20(tokenContract).transfer(treasury,penalty);
                }

            rewardPool(rewardPoolAddress).imposeRewardPenalty(_stakeId,stakedPeriod,_stake,_totalStake);
            isPenalized[_stakeId]= true;
        }
        bool result=IERC20(tokenContract).transfer( msg.sender , _stake - penalty);
        require(result,"error transfering tokens to holder");
        stake storage temp = getStakeById[_stakeId];
        temp.stakeAmount= getStakeAmount(_stakeId) - _stake;
        if(stakeOf(msg.sender) == 0) isStakeHolder[msg.sender] = false;        
        return true;
    }

    function getPrincipalPenalty(uint256 _stake) internal  pure   returns(uint256 penalty){    
       penalty = _stake * 5 / 100 ;

    } 

    function stakeOf(address _stakeholder)
        public
        view
        returns(uint256 )
    {
        uint256 totalStake = 0;
        for(uint256 i=0;i<addressToStakes[_stakeholder].length;i++){
            uint256 temp= getStakeAmount(addressToStakes[_stakeholder][i]);
            totalStake+=temp;
        }
        return totalStake;
    }
    function getStartTimeOfStake(uint256 _stakeId) view public returns(uint256){
        stake memory temp = getStakeById[_stakeId];
        return temp.stackingStartTime;
    }

    function getHolderByStakeId(uint256 _stakeId) public view returns(address){
        stake memory temp = getStakeById[_stakeId];
        return temp.holder;
    }

    function isDurationBound(uint256 _stakeId) public view returns(bool){
        stake memory temp = getStakeById[_stakeId];
        if(temp.duration == 0) return false;
        return true;
    }

    function getStakeAmount(uint256 _stakeId) public view returns(uint256){
        stake memory temp = getStakeById[_stakeId];
        return temp.stakeAmount;
    }
    function getstakesTillEpoch(uint256 epochNumber) public view returns(uint256){
        uint256 result = 0;
        for(uint256 i=0;i<=epochNumber;i++){
            result+=stakesAddedInEpoch[i];
        }
        return result;
    }
    function getDuration(uint256 _stakeId) public view returns(uint256){
        stake memory temp = getStakeById[_stakeId];
        return temp.duration;
    }

    function getTotalNumberOfStakes() public view returns(uint256){
        return stakeId;
    }

    function getAddedEpoch(uint256 _stakeId) public view returns(uint256){
        return addedInEpoch[_stakeId];
    }





    function addRewardPoolAddress(address _rewardPool) public onlyOwner returns(bool){
        rewardPoolAddress=_rewardPool;
        return true;
    }

    function getactiveStakesTillEpoch(uint256 epoch) public view returns(uint256){
        uint256 result = 0;
        for(uint256 i=0;i<=epoch;i++){
            result = result + stakesAddedInEpoch[i] - stakesRemovedInEpoch[i];
        }
        return result;    
    }

    function getStakesOfAddress(address holder) public view returns(uint256[] memory){
          return addressToStakes[holder];
    }
}

contracts/uniswapFeeRouter.sol.content:
//SPDX-License-Identifier: MIT
pragma solidity ^0.8;
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
interface IUniswapV2Pair {
    function getReserves()
        external
        view
        returns (
            uint112 reserve0,
            uint112 reserve1,
            uint32 blockTimestampLast
        );

    function swap(
        uint256 amount0Out,
        uint256 amount1Out,
        address to,
        bytes calldata data
    ) external;
}


interface IWETH {
    function deposit() external payable;

    function transfer(address to, uint256 value) external returns (bool);

    function withdraw(uint256) external;
}

library SafeMath {
    function add(uint256 x, uint256 y) internal pure returns (uint256 z) {
        require((z = x + y) >= x, "ds-math-add-overflow");
    }

    function sub(uint256 x, uint256 y) internal pure returns (uint256 z) {
        require((z = x - y) <= x, "ds-math-sub-underflow");
    }

    function mul(uint256 x, uint256 y) internal pure returns (uint256 z) {
        require(y == 0 || (z = x * y) / y == x, "ds-math-mul-overflow");
    }
}

library UniswapV2Library {
    using SafeMath for uint256;

    // returns sorted token addresses, used to handle return values from pairs sorted in this order
    function sortTokens(address tokenA, address tokenB)
        internal
        pure
        returns (address token0, address token1)
    {
        require(tokenA != tokenB, "UniswapV2Library: IDENTICAL_ADDRESSES");
        (token0, token1) = tokenA < tokenB
            ? (tokenA, tokenB)
            : (tokenB, tokenA);
        require(token0 != address(0), "UniswapV2Library: ZERO_ADDRESS");
    }

    // calculates the CREATE2 address for a pair without making any external calls
    function pairFor(
        address factory,
        address tokenA,
        address tokenB,
        bytes32 hash
    ) internal pure returns (address pair) {
        (address token0, address token1) = sortTokens(tokenA, tokenB);
        // bytes32 hash = 0x96e8ac4277198ff8b6f785478aa9a39f403cb768dd02cbee326c3e7da348845f;
        pair = address(
            uint160(
                uint256(
                    keccak256(
                        abi.encodePacked(
                            hex"ff",
                            factory,
                            keccak256(abi.encodePacked(token0, token1)),
                            hash // init code hash
                        )
                    )
                )
            )
        );
    }

    // fetches and sorts the reserves for a pair
    function getReserves(
        address factory,
        address tokenA,
        address tokenB,
        bytes32 hash
    ) internal view returns (uint256 reserveA, uint256 reserveB) {
        (address token0, ) = sortTokens(tokenA, tokenB);
        (uint256 reserve0, uint256 reserve1, ) = IUniswapV2Pair(
            pairFor(factory, tokenA, tokenB, hash)
        ).getReserves();
        (reserveA, reserveB) = tokenA == token0
            ? (reserve0, reserve1)
            : (reserve1, reserve0);
    }

    // given some amount of an asset and pair reserves, returns an equivalent amount of the other asset
    function quote(
        uint256 amountA,
        uint256 reserveA,
        uint256 reserveB
    ) internal pure returns (uint256 amountB) {
        require(amountA > 0, "UniswapV2Library: INSUFFICIENT_AMOUNT");
        require(
            reserveA > 0 && reserveB > 0,
            "UniswapV2Library: INSUFFICIENT_LIQUIDITY"
        );
        amountB = amountA.mul(reserveB) / reserveA;
    }

    // given an input amount of an asset and pair reserves, returns the maximum output amount of the other asset
    function getAmountOut(
        uint256 amountIn,
        uint256 reserveIn,
        uint256 reserveOut
    ) internal pure returns (uint256 amountOut) {
        require(amountIn > 0, "UniswapV2Library: INSUFFICIENT_INPUT_AMOUNT");
        require(
            reserveIn > 0 && reserveOut > 0,
            "UniswapV2Library: INSUFFICIENT_LIQUIDITY"
        );
        uint256 amountInWithFee = amountIn.mul(997);
        uint256 numerator = amountInWithFee.mul(reserveOut);
        uint256 denominator = reserveIn.mul(1000).add(amountInWithFee);
        amountOut = numerator / denominator;
    }

    // given an output amount of an asset and pair reserves, returns a required input amount of the other asset
    function getAmountIn(
        uint256 amountOut,
        uint256 reserveIn,
        uint256 reserveOut
    ) internal pure returns (uint256 amountIn) {
        require(amountOut > 0, "UniswapV2Library: INSUFFICIENT_OUTPUT_AMOUNT");
        require(
            reserveIn > 0 && reserveOut > 0,
            "UniswapV2Library: INSUFFICIENT_LIQUIDITY"
        );
        uint256 numerator = reserveIn.mul(amountOut).mul(1000);
        uint256 denominator = reserveOut.sub(amountOut).mul(997);
        amountIn = (numerator / denominator).add(1);
    }

    // performs chained getAmountOut calculations on any number of pairs
    function getAmountsOut(
        address factory,
        uint256 amountIn,
        address[] memory path,
        bytes32 hash
    ) internal view returns (uint256[] memory amounts) {
        require(path.length >= 2, "UniswapV2Library: INVALID_PATH");
        amounts = new uint256[](path.length);
        amounts[0] = amountIn;
        for (uint256 i; i < path.length - 1; i++) {
            (uint256 reserveIn, uint256 reserveOut) = getReserves(
                factory,
                path[i],
                path[i + 1],
                hash
            );
            amounts[i + 1] = getAmountOut(amounts[i], reserveIn, reserveOut);
        }
    }

    // performs chained getAmountIn calculations on any number of pairs
    function getAmountsIn(
        address factory,
        uint256 amountOut,
        address[] memory path,
        bytes32 hash
    ) internal view returns (uint256[] memory amounts) {
        require(path.length >= 2, "UniswapV2Library: INVALID_PATH");
        amounts = new uint256[](path.length);
        amounts[amounts.length - 1] = amountOut;
        for (uint256 i = path.length - 1; i > 0; i--) {
            (uint256 reserveIn, uint256 reserveOut) = getReserves(
                factory,
                path[i - 1],
                path[i],
                hash
            );
            amounts[i - 1] = getAmountIn(amounts[i], reserveIn, reserveOut);
        }
    }
}

// helper methods for interacting with ERC20 tokens and sending ETH that do not consistently return true/false
library TransferHelper {
    function safeApprove(
        address token,
        address to,
        uint256 value
    ) internal {
        // bytes4(keccak256(bytes('approve(address,uint256)')));
        (bool success, bytes memory data) = token.call(
            abi.encodeWithSelector(0x095ea7b3, to, value)
        );
        require(
            success && (data.length == 0 || abi.decode(data, (bool))),
            "TransferHelper: APPROVE_FAILED"
        );
    }

    function safeTransfer(
        address token,
        address to,
        uint256 value
    ) internal {
        // bytes4(keccak256(bytes('transfer(address,uint256)')));
        (bool success, bytes memory data) = token.call(
            abi.encodeWithSelector(0xa9059cbb, to, value)
        );
        require(
            success && (data.length == 0 || abi.decode(data, (bool))),
            "TransferHelper: TRANSFER_FAILED"
        );
    }

    function safeTransferFrom(
        address token,
        address from,
        address to,
        uint256 value
    ) internal {
        // bytes4(keccak256(bytes('transferFrom(address,address,uint256)')));
        (bool success, bytes memory data) = token.call(
            abi.encodeWithSelector(0x23b872dd, from, to, value)
        );
        require(
            success && (data.length == 0 || abi.decode(data, (bool))),
            "TransferHelper: TRANSFER_FROM_FAILED"
        );
    }

    function safeTransferETH(address to, uint256 value) internal {
        (bool success, ) = to.call{value: value}(new bytes(0));
        require(success, "TransferHelper: ETH_TRANSFER_FAILED");
    }
}

abstract contract Context {
    function _msgSender() internal view virtual returns (address) {
        return msg.sender;
    }

    function _msgData() internal view virtual returns (bytes calldata) {
        return msg.data;
    }
}

abstract contract Ownable is Context {
    address private _owner;

    event OwnershipTransferred(
        address indexed previousOwner,
        address indexed newOwner
    );

    constructor() {
        _transferOwnership(_msgSender());
    }

    function owner() public view virtual returns (address) {
        return _owner;
    }

    modifier onlyOwner() {
        require(owner() == _msgSender(), "Ownable: caller is not the owner");
        _;
    }

    function renounceOwnership() public virtual onlyOwner {
        _transferOwnership(address(0));
    }

    function transferOwnership(address newOwner) public virtual onlyOwner {
        require(
            newOwner != address(0),
            "Ownable: new owner is the zero address"
        );
        _transferOwnership(newOwner);
    }

    function _transferOwnership(address newOwner) internal virtual {
        address oldOwner = _owner;
        _owner = newOwner;
        emit OwnershipTransferred(oldOwner, newOwner);
    }
}

contract FeeRouter is Ownable {
    using SafeMath for uint256;

    // address public immutable factory;
    address public immutable WETH;
    uint256 public constant feeDenom = 10000;


    modifier ensure(uint256 deadline) {
        require(deadline >= block.timestamp, "UniswapV2Router: EXPIRED");
        _;
    }

    constructor(
        // address _factory,
        address _WETH
    ) Ownable() {
        // factory = _factory;
        WETH = _WETH;
    }


    receive() external payable {
        assert(msg.sender == WETH); // only accept ETH via fallback from the WETH contract
    }

    // **** SWAP ****
    // requires the initial amount to have already been sent to the first pair
    function _swap(
        address factory,
        uint256[] memory amounts,
        address[] memory path,
        address _to,
        bytes32 hash
    ) internal virtual {
        for (uint256 i; i < path.length - 1; i++) {
            (address input, address output) = (path[i], path[i + 1]);
            (address token0, ) = UniswapV2Library.sortTokens(input, output);
            uint256 amountOut = amounts[i + 1];
            (uint256 amount0Out, uint256 amount1Out) = input == token0
                ? (uint256(0), amountOut)
                : (amountOut, uint256(0));
            address to = i < path.length - 2
                ? UniswapV2Library.pairFor(factory, output, path[i + 2], hash)
                : _to;
            IUniswapV2Pair(
                UniswapV2Library.pairFor(factory, input, output, hash)
            ).swap(amount0Out, amount1Out, to, new bytes(0));
        }
    }

    function swapExactETHForTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline,
        address factory,
        bytes32 hash
    )
        external
        payable
        virtual
        ensure(deadline)
        returns (uint256[] memory amounts)
    {
        require(path[0] == WETH, "UniswapV2Router: INVALID_PATH");
        require(amountIn <= msg.value);
        amounts = UniswapV2Library.getAmountsOut(factory, amountIn, path, hash);
        require(
            amounts[amounts.length - 1] >= amountOutMin,
            "UniswapV2Router: INSUFFICIENT_OUTPUT_AMOUNT"
        );
        IWETH(WETH).deposit{value: amounts[0]}();
        assert(
            IWETH(WETH).transfer(
                UniswapV2Library.pairFor(factory, path[0], path[1], hash),
                amounts[0]
            )
        );
        _swap(factory, amounts, path, to, hash);
    }

    // **** SWAP (supporting fee-on-transfer tokens) ****
    // requires the initial amount to have already been sent to the first pair
    function _swapSupportingFeeOnTransferTokens(
        address factory,
        address[] memory path,
        address _to,
        bytes32 hash
    ) internal virtual {
        for (uint256 i; i < path.length - 1; i++) {
            (address input, address output) = (path[i], path[i + 1]);
            (address token0, ) = UniswapV2Library.sortTokens(input, output);
            IUniswapV2Pair pair = IUniswapV2Pair(
                UniswapV2Library.pairFor(factory, input, output, hash)
            );
            uint256 amountInput;
            uint256 amountOutput;
            {
                // scope to avoid stack too deep errors
                (uint256 reserve0, uint256 reserve1, ) = pair.getReserves();
                (uint256 reserveInput, uint256 reserveOutput) = input == token0
                    ? (reserve0, reserve1)
                    : (reserve1, reserve0);
                amountInput = IERC20(input).balanceOf(address(pair)).sub(
                    reserveInput
                );
                amountOutput = UniswapV2Library.getAmountOut(
                    amountInput,
                    reserveInput,
                    reserveOutput
                );
            }
            (uint256 amount0Out, uint256 amount1Out) = input == token0
                ? (uint256(0), amountOutput)
                : (amountOutput, uint256(0));
            address to = i < path.length - 2
                ? UniswapV2Library.pairFor(factory, output, path[i + 2], hash)
                : _to;
            pair.swap(amount0Out, amount1Out, to, new bytes(0));
        }
    }

    function swapExactETHForTokensSupportingFeeOnTransferTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline,
        address factory,
        bytes32 hash
    ) external payable virtual ensure(deadline) {
        require(path[0] == WETH, "UniswapV2Router: INVALID_PATH");
        require(amountIn <= msg.value);

        IWETH(WETH).deposit{value: amountIn}();
        assert(
            IWETH(WETH).transfer(
                UniswapV2Library.pairFor(factory, path[0], path[1], hash),
                amountIn
            )
        );
        uint256 balanceBefore = IERC20(path[path.length - 1]).balanceOf(to);
        _swapSupportingFeeOnTransferTokens(factory, path, to, hash);
        require(
            IERC20(path[path.length - 1]).balanceOf(to).sub(balanceBefore) >=
                amountOutMin,
            "UniswapV2Router: INSUFFICIENT_OUTPUT_AMOUNT"
        );
    }
}

contracts/volary.sol.content:
// SPDX-License-Identifier: GPLv3
pragma solidity ^0.8.4;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/// @custom:security-contact aaraamasree@nes.tech
contract Volary is ERC20, ERC20Burnable, Pausable, Ownable {
    constructor() ERC20("testVolary", "tVLRY") {
        _mint(msg.sender, 1000000000 * 10 ** decimals());
    }

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    function mint(address to, uint256 amount) public onlyOwner {
        _mint(to, amount);
    }

    function _beforeTokenTransfer(address from, address to, uint256 amount)
        internal
        whenNotPaused
        override
    {
        super._beforeTokenTransfer(from, to, amount);
    }
}

